Load data

tips &lt;- read_csv("data/tip-data.csv") |&gt;
  filter(!is.na(Party))


# relevel factors
tips &lt;- tips |&gt;
  mutate(
    Meal = fct_relevel(Meal, "Lunch", "Dinner", "Late Night"),
    Age  = fct_relevel(Age, "Yadult", "Middle", "SenCit")
  )

Packages

library(tidyverse)
library(tidymodels)
library(knitr)

heart_disease &lt;- read_csv("data/framingham.csv") |&gt;
  select(totChol, TenYearCHD) |&gt;
  drop_na() |&gt;
  mutate(high_risk = as.factor(TenYearCHD)) |&gt;
  select(totChol, high_risk)

Function

# function to calculate model fit statistics
calc_model_stats &lt;- function(x) {
  glance(extract_fit_parsnip(x)) |&gt;
    select(adj.r.squared, AIC, BIC)
}

