movies |&gt;
  filter(genre %in% genre_list) |&gt; 
  group_by(genre,year) |&gt;
  summarise(avg_gross = mean(gross)) |&gt;
  gf_point(avg_gross ~ year, color = ~genre) |&gt;
  gf_line() |&gt; 
  gf_labs(y = "Average Gross Revenue (in US Dollars)",
    title = "Gross Revenue Over Time")














